	//	return contractAddr, nil, err
	//}
	hexByteCode := contractBinary
	//	abiBytes, err := ioutil.ReadFile("testdata/" + filename + ".abi")
	//	if err != nil {
	//		return contractAddr, nil, err
	//	}
	abiBytes := zbGameModeABI
